mapper.update:
update donation_transactions
set subscription_id = #{subscriptionId,jdbcType=INTEGER},
  amount = #{amount,jdbcType=DECIMAL},
  paid_at = #{paidAt,jdbcType=TIMESTAMP},
  pg_transaction_data = #{pgTransactionData,jdbcType=LONGVARCHAR}
where transaction_id = #{transactionId,jdbcType=VARCHAR}

mapper.select:
select transaction_id, subscription_id, amount, paid_at, pg_transaction_data
from donation_transactions
where transaction_id = #{transactionId,jdbcType=VARCHAR}
