mapper.update:
update shelter_registrations
set applicant_id = #{applicantId,jdbcType=INTEGER},
  reviewed_by = #{reviewedBy,jdbcType=INTEGER},
  shelter_id = #{shelterId,jdbcType=INTEGER},
  review_notes = #{reviewNotes,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, applicant_id, reviewed_by, shelter_id, review_notes
from shelter_registrations
where id = #{id,jdbcType=INTEGER}
